CREATE TABLE `contribution` (
  `id` int(11) NOT NULL,
  `username` varchar(255) NOT NULL,
  `aspect` varchar(255) NOT NULL COMMENT '方面',
  `title` varchar(255) DEFAULT NULL,
  `content` text,
  `time` datetime NOT NULL,
  `read` enum('0','1') DEFAULT '0' COMMENT '0:站长未读;1:站长已读',
