--
-- 表的结构 `user`
--

CREATE TABLE `user` (
  `id` int(10) NOT NULL COMMENT '用户标识符',
  `username` varchar(20) NOT NULL COMMENT '账号',
  `nickname` varchar(20) NOT NULL COMMENT '昵称',
  `gender` enum('男','女','保密') DEFAULT '保密' COMMENT '性别',
  `password` varchar(20) NOT NULL COMMENT '密码',
  `education` varchar(5) DEFAULT NULL COMMENT '学历',
  `city` varchar(2) DEFAULT NULL COMMENT '所在城市',
  `hobby` varchar(5) DEFAULT NULL COMMENT '浏览偏好',
  `question` varchar(30) DEFAULT NULL COMMENT '安全问题',
  `answer` varchar(30) DEFAULT NULL COMMENT '问题答案',
  `phone` varchar(14) DEFAULT NULL COMMENT '手机号',
  `email` varchar(20) DEFAULT NULL COMMENT '电子邮箱',
  `self-introduction` text COMMENT '自我介绍',
  `user_group` enum('会员','管理员') NOT NULL DEFAULT '会员' COMMENT '用户组',
  `regtime` datetime NOT NULL COMMENT '注册时间',
  `style` varchar(5) NOT NULL COMMENT '注册方式'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='网站（注册）用户表';

